CREATE VIEW "public"."v_users" AS (select "auth"."users"."id", "auth"."users"."email", "auth"."users"."last_sign_in_at", "user_profile"."display_name", "user_profile"."github", "user_profile"."website", "user_profile"."bluesky", "user_profile"."mastodon", "user_profile"."twitter", "user_profile"."created_at", "user_profile"."updated_at" from "auth"."users" left join "user_profile" on "user_profile"."user_id" = "auth"."users"."id");